import http from '../utils/request'

// 请求首页数据
export const getData  = () => {
    // 返回一个promise对象
    return http.get('/home/getData')
}

export const getUser = (params) => {
    // 返回用户列表
    return http.get('/user/getUser', params)
}

export const addUser = () => {
    return http.post('/user/add')
}
